import React, { Component } from 'react'
import apiFunc from '../../../api.jsx'
import { CaseWorkerCard } from './CaseWorkerCard.jsx'
import { SubjectCard } from './SubjectCard.jsx'
import { FamilyList } from '../list/FamilyList.jsx'
import CaseGraph from './CaseGraph.jsx'

const api = apiFunc()

class CaseView extends Component {
  constructor(props) {
    super(props)
    this.state = {
      ffCase: null,
      error: null
    }
  }

  componentDidMount() {
    this.reloadCase()
  }

  reloadCase() {
    api.getCase(this.props.params.caseId)
      .then(result => {
        this.setState(Object.assign({}, this.state, { ffCase: result }))
      })
      .catch(err => this.setState({ error: err }))
  }
  render() {
    const { ffCase, error } = this.state
    if (error) {
      return <div className="container-fluid">
        <div className="alert alert-danger">Error: {error.message}</div>
      </div>
    } else if (!ffCase) {
      return <div className="container-fluid">
        <div className="alert alert-info">Loading...</div>
      </div>
    } else {
      const subject = ffCase.subjects[0];
      return <div className="container-fluid">
        <div className="media">
          <div className="media-body">
            <h4 className="media-heading">Case Profile: {subject.person.givenNames}</h4>
          </div>
          <div className="media-right">
            <img alt="64x64"
              className="media-object"
              data-src="holder.js/64x64"
              src="data:image/svg+xml;base64,"
              data-holder-rendered="true"
              style={{ width: 64, height: 64 }} />
          </div>
        </div>
        <div className="row">
          <div className="col-md-6">
            <SubjectCard {...subject.person} />
          </div>
          <div className="col-md-6">
            <CaseGraph />
          </div>
        </div>
      </div>
    }
  }
}

export default CaseView
